import React, { useState } from "react";
import './Styles/App.css'
import Box9x9 from "./Components/Box9x9/Box9x9";

const red = 'rgb(220, 20, 60)';
const blue = 'rgb(0, 191, 255)';
const marker = 'rgb(0, 40, 255)';

function App() {
  let [lastColor, setLastColor] = useState('');
  let [lastId, setLastId] = useState('');
  let [lastBox, setLastBox] = useState('');
  //Меняет цвет блока, переключая его при ходе.
  document.getElementById('Five').style.border = ('3px solid ' + marker)
  function changeColor(sid, box_id){
    var box = document.getElementById(box_id);
    var elem = document.getElementById(sid); // Блок, на который мы кликаем
    var prevElem = document.getElementById(lastId); //Блок, на который мы в прошлый раз кликнули(по умолчанию == NUll)
    var typecolor = window.getComputedStyle(elem, null).backgroundColor; //Цвет заднего фона блока
    var bordercolor = window.getComputedStyle(elem, null).border; // Параметры границ блока
    var activebox = window.getComputedStyle(elem, null).border;
    if (typecolor === 'rgba(0, 0, 0, 0)'){ //проверка, что кликаем не на закрашенный блок
      if (lastColor === red){ //определяем цвет, с помощью предыдущего, чтобы не перекрашивать блоки.
        typecolor = blue;
        activebox = ('3px solid ' + marker)
        setLastColor(typecolor);
        setLastId(sid)
      }
      else{
        typecolor = red;
        activebox = ('3px solid ' + marker)
        setLastColor(typecolor);
        setLastId(sid)
    }
    box.style.border = activebox;
    elem.style.backgroundColor = typecolor;
    elem.style.border = ('2px solid ' + marker); // перекрашиваем границу последнего кликнутого блока
    if (prevElem != null){ //возвращаем цвет границы назад
      prevElem.style.border = ('2px solid gold');
    }
    console.log(sid, lastColor, lastId, typecolor, bordercolor, activebox); // Отладка
    }
  }
  return (
    <div className="App" >
      <Box9x9 getId={changeColor}/>
    </div>
  );
}

export default App;
