import React, { useState } from "react";
import './Styles/App.css'
import Block from "./Components/block/block";
import Box3x3 from "./Components/Box3x3/Box3x3";
import Box9x9 from "./Components/Box9x9/Box9x9";

function App() {
  const [sign, setSign] = useState('');
  function getId(element){
    setSign(element)
    console.log(sign)
  }
let box = document.getElementById(sign);
let isShown = false;

function toggleListVisibility(){
    if (isShown){
        box.style.backgroundColor = "Red";
        isShown = false;
    }else{
        box.style.backgroundColor = "Red";
        isShown = true;
    }
}

box.addEventListener('click', toggleListVisibility);
  return (
    <div className="App" >
      <div> {sign}</div>
      <Box9x9 getId={getId}/>
    </div>
  );
}

export default App;
