import React, { useState } from "react";
import './Styles/App.css'
import Box9x9 from "./Components/Box9x9/Box9x9";

const red = 'rgb(220, 20, 60)';
const blue = 'rgb(0, 191, 255)';
const marker = 'rgb(0, 40, 255)';

function App() {
  let [lastColor, setLastColor] = useState('');
  let [lastId, setLastId] = useState('');

  //Меняет цвет блока, переключая его при ходе.
  function changeColor(sid){
    setLastId(sid)
    var elem = document.getElementById(sid);
    var prevElem = document.getElementById(lastId);
    var typecolor = window.getComputedStyle(elem, null).backgroundColor;
    var bordercolor = window.getComputedStyle(elem, null).border;
    if (typecolor === 'rgba(0, 0, 0, 0)'){
      if (lastColor === red){
        typecolor = blue;
        setLastColor(typecolor);
        setLastId(sid)
      }
      else{
        typecolor = red;
        setLastColor(typecolor);
        setLastId(sid)
    }
    elem.style.backgroundColor = typecolor;
    elem.style.border = ('2px solid ' + marker);
    prevElem.style.border = ('2px solid gold');
    console.log(sid, lastColor, lastId, typecolor, bordercolor);
    }
  }
  return (
    <div className="App" >
      <Box9x9 getId={changeColor}/>
    </div>
  );
}

export default App;
